/**
 * Expands/flattens a type, so that it isn't defined by type-aliases.
 *
 * ```ts
 * import { IPalette } from '....';
 * type A = IPalette<{
 *    primary: "#3344dd";
 *    secondary: "#55ddff";
 * }, {
 *     light: string;
 *     dark: string;
 * }>;
 * type B = ExpandType<A> /* {
 *   primary: {
 *     light: string;
 *     dark: string;
 *   };
 *   secondary: {
 *     light: string;
 *     dark: string;
 *   };
 * };
 * ```
 */
export declare type ExpandType<T> = T extends object ? T extends infer O ? {
    [K in keyof O]: ExpandType<O[K]>;
} : never : T;
